mapper.select:
SELECT T.tName as theaterName, SUM(P.paymentFinal) as totalSales FROM payment P LEFT JOIN reservation R ON P.reservIdx = R.reservIdx LEFT JOIN time_table TT ON R.timeTableIdx = TT.timeTableIdx LEFT JOIN theater T ON TT.tIdx = T.tIdx LEFT JOIN movie M ON TT.mIdx = M.mIdx WHERE T.tName IS NOT NULL
${whereClause}
#{name}
GROUP BY T.tName ORDER BY totalSales DESC

SELECT M.name as theaterName, SUM(P.paymentFinal) as totalSales FROM payment P LEFT JOIN reservation R ON P.reservIdx = R.reservIdx LEFT JOIN time_table TT ON R.timeTableIdx = TT.timeTableIdx LEFT JOIN theater T ON TT.tIdx = T.tIdx LEFT JOIN movie M ON TT.mIdx = M.mIdx WHERE M.name IS NOT NULL
${whereClause}
#{name}
GROUP BY M.name ORDER BY totalSales DESC LIMIT 10
