mapper.select:
SELECT COUNT(*)
FROM payment p



            AND p.userIdx = #{userKey}


            AND p.nIdx = #{userKey}



        AND p.paymentStatus = #{statusFilter}


        AND YEAR(p.paymentDate) = #{yearFilter}


        AND p.paymentType = #{typeFilter}

-- 영화 예매 내역 (paymentType = 0) SELECT p.paymentType, p.paymentDate, p.orderId, p.paymentTransactionId AS paymentKey, p.paymentStatus, m.name AS itemTitle, m.poster AS itemPosterUrl, CONCAT(t.tName, ' ', s.sName) AS theaterInfo, tt.startTime AS screenDate, NULL AS prodPrice, 1 AS quantity -- 영화 예매는 수량을 1로 고정 FROM payment p JOIN reservation r ON p.reservIdx = r.reservIdx JOIN time_table tt ON r.timeTableIdx = tt.timeTableIdx JOIN movie m ON tt.mIdx = m.mIdx JOIN theater t ON tt.tIdx = t.tIdx JOIN screen s ON tt.sIdx = s.sIdx
p.paymentType = 0
AND p.userIdx = #{userKey}
AND p.nIdx = #{userKey}
AND p.paymentStatus = #{statusFilter}
AND YEAR(p.paymentDate) = #{yearFilter}
AND p.paymentType = #{typeFilter}
UNION ALL
-- 스토어 구매 내역 (paymentType = 1) SELECT p.paymentType, p.paymentDate, p.orderId, p.paymentTransactionId AS paymentKey, p.paymentStatus, pr.prodName AS itemTitle, pr.prodImg AS itemPosterUrl, NULL AS theaterInfo, NULL AS screenDate, pr.prodPrice, -- 수정 2: 총 결제금액 / 상품가격을 나눠서 수량을 실시간으로 계산 CASE WHEN pr.prodPrice > 0 THEN (p.paymentTotal / pr.prodPrice) ELSE 1 END AS quantity FROM payment p JOIN product pr ON p.prodIdx = pr.prodIdx
p.paymentType = 1
AND p.userIdx = #{userKey}
AND p.nIdx = #{userKey}
AND p.paymentStatus = #{statusFilter}
AND YEAR(p.paymentDate) = #{yearFilter}
AND p.paymentType = #{typeFilter}
ORDER BY paymentDate DESC LIMIT #{begin}, #{end}
